function create_default_reports($is_upgrade = false, $reportlist = array())
{
    $stock_tags = array(
        "sales_marketing" => array("Sales and Marketing", "Stock Report"),
        "customer_service" => array("Customer Service", "Stock Report"),
        "data_privacy" => array("Data Privacy", "Stock Report"),
        "administrative" => array("Administrative", "Stock Report"),
    );

    $ootb_reports['administrative'] = get_admin_reports($is_upgrade);
    $ootb_reports['data_privacy'] = get_data_privacy_reports($is_upgrade);
    $ootb_reports['customer_service'] = get_customer_service_reports($is_upgrade);
    $ootb_reports['sales_marketing'] = get_sales_marketing_reports($is_upgrade);

    foreach ($ootb_reports as $key => $reports) {
        foreach ($reports as $report) {
            if (!empty($reportlist) && !in_array($report[1], $reportlist)) {
                continue;
            }
            $saved_report = BeanFactory::newBean('Reports');
            if ($report[3] == 'tabular') {
                $result = $saved_report->save_report(-1, 1, $report[1], $report[0], $report[3], $report[2], 1, '1', 'none', null, $report[5]);
            } else {
                $result = $saved_report->save_report(-1, 1, $report[1], $report[0], $report[3], $report[2], 1, '1', $report[4], null, $report[5]);
            }

            $saved_report->load_relationship("tag_link");

            $tagBean = new SugarFieldTag('tag');
            $relBeans = array();
            $addedTags = array();
            foreach ($stock_tags[$key] as $tag) {
                $relBeans[] = $tagBean->getTagBean($tag);
                $addedTags[strtolower($tag)] = $tag;
            }

            $tagBean->addTagsToBean($saved_report, $relBeans, "tag_link", $addedTags);
        }
    }
}

/**
 * Get the date for the current quarter
 *
 * @return array
 */
function get_quarter()
{
    $y = date('Y');
    $m = date('m');
    switch ($m) {
        case $m >= 1 && $m <= 3:
            $start = $y . '-01-01';
            $end = $y . '-03-31';
            break;
        case $m >= 4 && $m <= 6:
            $start = $y . '-04-01';
            $end = $y . '-06-30';
            break;
        case $m >= 7 && $m <= 9:
            $start = $y . '-07-01';
            $end = $y . '-09-30';
            break;
        case $m >= 10 && $m <= 12:
            $start = $y . '-10-01';
            $end = $y . '-12-31';
            break;
    }
    return array(
        'start' => $start,
        'end' => $end,
    );
}
